sql.where:
status != 'd'
and bill_code like concat('%', #{billCode}, '%')
and bill_name like concat('%', #{billName}, '%')
and bill_type = #{billType,jdbcType=VARCHAR}
and from_region = #{fromRegion,jdbcType=INTEGER}
and to_region = #{toRegion,jdbcType=INTEGER}
and status = #{status,jdbcType=VARCHAR}
and deal_status = #{dealStatus,jdbcType=VARCHAR}
