sql.where:
status != 'd'
 and item_id = #{itemId}
 and pn = #{pn}
 and name like concat('%',#{goods},'%')
 and
     ( pn = #{goods} or barcode = #{goods} or name like concat('%',#{goods},'%') )

 and belong = #{belong}
 and warehouse = #{warehouse}

 and id = #{stockId}
 and location = #{locationCode}
 and section = #{sectionCode}

mapper.select:
select * from (
select pn,item_id, sum(quantity * pack_quantity) quantity
, warehouse

from WM_INVENTORY

warehouse = #{warehouse}


GROUP BY pn, item_id
, warehouse

) su, WM_ITEM it where it.id = su.item_id
and it.name like concat('%', #{goodsName}, '%')
order by ${_BY}

select count(1) from ( select pn,item_id, sum(quantity * pack_quantity) quantity , warehouse
from WM_INVENTORY
warehouse = #{warehouse}
GROUP BY pn, item_id , warehouse
) su, WM_ITEM it where it.id = su.item_id and it.name like concat('%', #{goodsName}, '%')

mapper.delete:
update WM_INVENTORY
set status = 'd', enter_at = now()
where id = #{stockId,jdbcType=INTEGER}
