sql.where:
status != 'd'

    and (pn like concat('%', #{_SH}, '%') or name like concat('%', #{_SH}, '%'))


    and name like concat('%', #{goodsName}, '%')


    and pn like concat('%', #{pn}, '%')


    and pn = #{goodsCode}


    and barcode like concat('%', #{barcode}, '%')


    and category_id = #{category}


    and brand = #{brand}

mapper.delete:
update WM_ITEM
set status = 'd', enter_at = now()
where id = #{id, jdbcType=INTEGER}

mapper.update:
update WM_ITEM
set avg_price = #{avgPrice,jdbcType=NUMERIC}
where id = #{itemId,jdbcType=INTEGER}
