sql.where:
status != 'd'
and org_code = #{orgCode}
and pn = #{pn}
and bill_code = #{billCode}
and bill_id = #{billId}
and origin_bill = #{originBill}
and bill_type = #{type}
and batch_code = #{batchCode}

      and adjust_quantity < real_quantity and target_org_type = 'store'

mapper.select:
select
from WM_WORK_DETAIL
order by id order by ${_BY}

mapper.delete:
update WM_WORK_DETAIL
set status = 'd',enter_at = now()
where id = #{id,jdbcType=BIGINT}

update WM_WORK_DETAIL
set status = 'd',enter_at = now()
where bill_id = #{value,jdbcType=INTEGER}

update WM_WORK_DETAIL
set status = 'd',enter_at = now()
where origin_bill = #{value,jdbcType=VARCHAR}
